 "PhyExam":"string" ; //生命体征与体格检查
 "LabExam":"string"   ;   // 实验室检查
 "ImageExam":"string"    ;  // 影像学检查
 "CardioExam":"string"  ;    // 心肺功能检查
 "SpecExam":"string"   ;   // 专科特殊检查
 "AnestRisk":"string"    ;  // 麻醉评估及风险评估
}

API 3:AdmExam（入院检查）  //Admission examination
描述: 获取患者的入院检查项目  
输入参数: {
PatientID (string, 必填);
ICD (string, 必填): ICD-10代码;  
}
输出参数:  {
 "SchedulePoint":"string" ; // 临床时间点  
 "PhyExam":"string" ; //生命体征与体格检查
 "LabExam":"string"  ;    // 实验室检查
 "ImageExam":"string";      // 影像学检查
 "ECGFunc":"string"    ;  // 心电与功能检查
 "SpecExam":"string"  ;    // 专科特殊检查
}

API 4:EarlyPostopeRecovery（术后早期恢复）  //Early postoperative recovery
描述: 获取患者的早期术后恢复注意事项 
输入参数: {
PatientID (string, 必填);
ICD (string, 必填): ICD-10代码;  
}
输出参数:  {
 "SchedulePoint":"string"  ;// 临床时间点  
 "ClinExam":"string"    ;  // 临床检查 
 "Medication":"string"  ;    // 用药计划
}

API 5:DischargeCriteria（出院标准判断）  //Discharge criteria
描述: 获取患者的出院标准判断结果  
输入参数: {
PatientID (string, 必填);
ICD (string, 必填): ICD-10代码;  
Recovery (string, 必填): 症状、检验检查和病情恢复情况;  
}
输出参数:  {
 "SchedulePoint":"string" ; // 临床时间点  
 "Discharge":"string"  ;    // 是否满足出院标准 
}

API 6:TreatOpt（治疗方案选择）  //Treatment options
描述: 获取患者的推荐治疗方案  
输入参数: {
PatientID (string, 必填);
ICD (string, 必填): ICD-10代码;  
Condition (string, 选填): 治疗方案选择依据;  
}
输出参数:  {
